import { motion, useAnimation } from "framer-motion";
import styled from "styled-components";

const NavbarCompopentsStyle = styled(motion.div)`
  position: fixed;
  top: 0;
  height: 5vw;
  width: 90%;
  padding: 0 5% 0 5%;
  z-index: 10000;
  font-size: 18px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: #191919;
  font-size: 1.3vw;
  .logo {
    width: 60%;
    font-size: 2vw;
    color: #f2c14e;
    cursor: pointer;
  }
  span,
  .home {
    color: #8a8888;
    cursor: pointer;
  }
  .home {
    color: #f8f8ff;
  }
  @media (max-width: 1430px) {
    .logo {
      width: 50%;
    }
  }
  .dropDown {
    position: absolute;
    top: 5vw;
    height: auto;
    width: auto;
    background-color: blanchedalmond;
    display: flex;
    flex-direction: column;
    cursor: auto;
    padding: 0 1vw 0 1vw;
    .dropDownItem {
      margin-top: 2vw;
    }
    .dropDownItem:first-child {
      margin: 0;
    }
  }
`;

const NavbarContent = [
  "home",
  "Five cols",
  "Fewer cols",
  "Templates",
  "Other pages",
];

const HomeNavbarDown = ["style 1", "style 2", "style 2"];

const DropDown = ({ item }) => {
  return (
    <div className="dropDown">
      {HomeNavbarDown.map((e, i) => (
        <span className="dropDownItem" key={i}>
          {e}
        </span>
      ))}
    </div>
  );
};

const NavbarComponent = () => {
  const NavbarMotionController = {
    home: useAnimation(),
    Five_Cols: useAnimation(),
    Fewer_cols: useAnimation(),
    Templates: useAnimation(),
    Other_pages: useAnimation(),
  };
  const NavbarDropDown = {
    home: useAnimation(),
    Five_Cols: useAnimation(),
    Fewer_cols: useAnimation(),
    Templates: useAnimation(),
    Other_pages: useAnimation(),
  };

  let selectedItem = 0;

  NavbarMotionController[Object.keys(NavbarMotionController)[0]].start({
    color: "#f8f8ff",
  });

  return (
    <NavbarCompopentsStyle>
      <span className="logo"> DeepEnd </span>
      {NavbarContent.map((e, i) => {
        return (
          <motion.span
            key={i}
            className={e}
            animate={
              NavbarMotionController[Object.keys(NavbarMotionController)[i]]
            }
            onHoverStart={() => {
              if (selectedItem !== i)
                NavbarMotionController[
                  Object.keys(NavbarMotionController)[i]
                ].start({
                  color: "#f2c14e",
                });
            }}
            onHoverEnd={() => {
              if (selectedItem !== i)
                NavbarMotionController[
                  Object.keys(NavbarMotionController)[i]
                ].start({
                  color: "#8a8888",
                });
            }}
            onClick={() => {
              if (selectedItem !== i) {
                NavbarMotionController[
                  Object.keys(NavbarMotionController)[i]
                ].start({
                  color: "#f8f8ff",
                });

                NavbarMotionController[
                  Object.keys(NavbarMotionController)[selectedItem]
                ].start({ color: "#8a8888" });
                selectedItem = i;
              }
            }}
          >
            {e}
            <DropDown item={e} />
          </motion.span>
        );
      })}
    </NavbarCompopentsStyle>
  );
};

export default NavbarComponent;
