    height: auto;
    .logo {
      margin-top: 30px;
      font-size: 100px;
      margin-bottom: 30px;
      color: #f8f8ff;
    }
    .welcome,
    .text {
      color: #f8f8ff;
      width: 300px;
    }
  }
  @media (min-width: 1450px) {
    background-position: 0 -60vw;
  }
