import { EventEmitter } from "events";

const checkScrollDirection = (e) => {
  if (e.wheelDelta) {
    return e.wheelDelta > 0;
  }
  return e.deltaY < 0;
};

class event extends EventEmitter {
  isScrollingToBottom(cb) {
    return this.on("scrollToBottom", cb);
  }
  isScrollToTop(cb) {
    return this.on("scrollToTop", cb);
  }
  ditectScrollDirection(e) {
    if (checkScrollDirection(e)) {
      return this.emit("scrollToTop");
    } else {
      return this.emit("scrollToBottom");
    }
  }
}

class MyEmitter extends EventEmitter {}

const myEmitter = new MyEmitter();
myEmitter.on("event", () => {
  console.log("an event occurred!");
});
myEmitter.emit("event");

const { isScrollToTop, isScrollingToBottom, ditectScrollDirection } =
  new event();
export default { isScrollToTop, isScrollingToBottom, ditectScrollDirection };
