import styled from "styled-components";
import { motion } from "framer-motion";
import AuthorComponent from "./author.component";

const SlideAuthorsStyle = styled(motion.div)`
  background-color: #e5e5e5;
  margin-bottom: 50px;
  display: flex;
  flex-wrap: nowrap;
  flex-direction: column;
  padding: 20px;

  .title {
    cursor: auto;
    position: relative;
    width: 100%;
    font-size: 20px;
    color: #1b1811;
    margin-bottom: 10px;
    padding-bottom: 10px;
    padding-left: 0;
    background-color: inherit;
  }
  @media (max-width: 1250px) {
    flex-direction: row;
  }
`;

const SlideAuthors = () => {
  return (
    <SlideAuthorsStyle>
      <span className="title">Discover more authors</span>
      {[1, 1, 1, 1, 1].map((e, i) => {
        return (
          <AuthorComponent
            styles="
            backgroud-color : #111111 ;
            width : calc(100% - 20px);
            margin-bottom : 20px ;
            padding : 10px ;
            .authorDetails {
                .name {
                font-size: 16px;
                color: #223222;
                }
            }
            "
            styleAnimation={{ backgroundColor: "#f8f8f8" }}
          />
        );
      })}
    </SlideAuthorsStyle>
  );
};

export default SlideAuthors;
