import styled from "styled-components";
import { motion } from "framer-motion";

const BecomeAuthorStyle = styled(motion.div)`
  display: flex;
  flex-direction: column;
  width: 90vw;
  margin-left: 5vw;
  margin-top: 150px;
  margin-bottom: 100px;
  max-width: 1800px;
  .topTitle {
    font-size: 16px;
    color: #c1c0c4;
    margin-bottom: 30px;
  }
  .botton {
    position: relative;
    padding: 10px;
    background-color: #f2c14e;
    width: 200px;
    display: flex;
    align-items: baseline;
    justify-content: center;
    font-size: 20px;
    margin-top: 5vw;
