  .title {
    display: flex;
    align-items: flex-end;
    position: relative;
    height: auto;
    .mainTitle {
      font-size: 50px;
      color: #f8f8ff;
      width: 90%;
      bottom: 0;
    }
    .subTitle {
      width: 100%;
      color: #8a8888;
    }
  }
  @media (max-width: 1150px) {
    height: auto;
    .title {
      display: flex;
      flex-direction: column;
      align-items: flex-start;
      * {
        left: 0;
        width: 90vw;
        margin-bottom: 50px;
      }
    }
  }
  @media (min-width: 2000px) {
    width: calc(2000px - 5vw);
    margin-left: calc(55vw - 1000px);
  }
`;
const BecomeAuthor = () => {
