import styled from "styled-components";
import { motion } from "framer-motion";
import ArticleStatistic from "./articleStatisti.component";

const CommentsComponentStyle = styled(motion.div)`
  position: relative;
  display: flex;
  flex-direction: column;
  border-left: 1px solid #f8f8ff;
  padding-left: 50px;
  width: 800px;
  padding-top: 50px;
  .commentInfo {
    margin-bottom: 20px;
    height: 50px;
    display: flex;
    flex-direction: row;
    align-items: center;
    .userName {
      color: #f2c14e;
      font-size: 24px;
      margin-left: 20px;
    }
    .userPic {
      height: 50px;
      width: 50px;
      background-color: chocolate;
      border-radius: 100%;
    }
    .more {
      position: absolute;
      right: 0;
      border-radius: 100%;
      float: right;
      height: 50px;
      width: 50px;
      background-color: burlywood;
    }
  }
  .content {
    font-size: 24px;
    color: #f8f8ff;
  }
  .replay {
    position: relative;
    margin-left: 0px;
    height: auto;
    width: 500px;
    padding-top: 50px;
  }
`;

const CommentsComponent = ({ array = [1, 1, 1] }) => {
  return (
    <CommentsComponentStyle>
      <div className="commentInfo">
        <div className="userPic" />
        <span className="userName">tahar belghitri</span>
        <div className="more" />
      </div>
      <span className="content">
        Praesent id massa id nisl venenatis lacinia. Aenean sit amet justo.
        Morbi ut odio.
      </span>
      <ArticleStatistic
        styles={".views { display : none }  margin-top : 30px ;"}
      />
      <div className="replay">
        {[1, 1, 1].map((e, i) => {
          return (
            <CommentsComponentStyle key={i}>
              <div className="commentInfo">
                <div className="userPic" />
                <span className="userName">tahar belghitri</span>
                <div className="more" />
              </div>
              <span className="content">
                Praesent id massa id nisl venenatis lacinia. Aenean sit amet
                justo. Morbi ut odio.
              </span>
              <ArticleStatistic
                styles={".views { display : none }  margin-top : 30px ;"}
              />
            </CommentsComponentStyle>
          );
        })}
      </div>
    </CommentsComponentStyle>
  );
};
export default CommentsComponent;
