import styled from "styled-components";
import { motion } from "framer-motion";

const ArticleStatisticStyle = styled(motion.div)`
  display: flex;
  flex-direction: row;
  font-size: 16px;
  .likes,
  .views,
  .comments {
    width: auto;
    height: 20px;
    display: flex;
    flex-direction: row;
    padding: 5px;
    justify-content: center;
    align-items: center;
    background-color: grey;
    margin-right: 20px;
  }
  .likeIcon,
  .viewIcon,
  .commentIcon {
    height: 20px;
    width: 20px;
    padding: 5px;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-right: 10px;
    background-color: green;
    border-radius: 100%;
  }
  .likes {
    color: #fa6d6d;
  }

  ${(p) => p.styles}
`;

const ArticleStatistic = ({ statistic, styles }) => {
  return (
    <ArticleStatisticStyle style={styles}>
      <div className="likes">
        <div className="likeIcon" />
        <span>{statistic || Math.floor(Math.random() * 500)} </span>
      </div>
      <div className="comments">
        <div className="commentIcon" />
        <span>{statistic || Math.floor(Math.random() * 100)} </span>
      </div>
      <div className="views">
        <div className="viewIcon" />
        <span>{statistic || Math.floor(Math.random() * 1000)} </span>
      </div>
    </ArticleStatisticStyle>
  );
};

export default ArticleStatistic;
