import styled from "styled-components";
import { motion } from "framer-motion";

const AuthorstListStyle = styled(motion.div)`
  position: relative;
  margin-top: 200px;
  width: 93vw;
  margin-left: 5vw;
  background-color: lightblue;
  height: auto;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  .Author {
    position: relative;
    margin-right: 50px;
    margin-top: 50px;
    width: 200px;
    height: 200px;
    background-color: lightcoral;
    .AuthorBg {
      position: absolute;
      top: 0;
      height: 100px;
      width: 200px;
      background-color: green;
    }
    .authorDeatils {
      z-index: 100;
      opacity: 1;
      position: absolute;
      bottom: 0;
      height: 120px;
      width: inherit;
      background-color: black;
      display: flex;
    }
  }
`;

const AuthorsList = () => {
  return (
    <AuthorstListStyle>
      {[1, 1, 1, 1, 1, 1, 1, 1, 1].map((e, i) => {
        return (
          <div className="Author">
            <div className="AuthorBg" />
            <div className="authorDeatils">
              <div className="AuthorPIc" />
              <span className="name">tahar belghitri</span>
              <span className="posts"> 201 posts </span>
              <span className="startedAt"> 02/02/2021 </span>
            </div>
          </div>
        );
      })}
    </AuthorstListStyle>
  );
};

export default AuthorsList;
