import styled from "styled-components";
import { motion } from "framer-motion";
import likeIcons from "../icons/like-icon.png";

const ArticleStatisticStyle = styled(motion.div)`
  display: flex;
  flex-direction: row;
  font-size: 16px;
  .likes,
  .views,
  .comments {
    width: auto;
    height: 20px;
    display: flex;
    flex-direction: row;
    padding: 5px;
    justify-content: center;
    align-items: center;
    margin-right: 20px;
    border-radius: 5px;
  }
  .likeIcon,
  .viewIcon,
  .commentIcon {
    height: 20px;
    width: 20px;
    padding: 5px;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-right: 10px;
    background-color: green;
    border-radius: 100%;
  }
  .likes {
    color: #fa6d6d;
  }
  .comments {
    color: #85ef90;
  }
  .views {
    color: #768bfb;
  }
  .likeIcon {
    padding: 5px;
    background-image: url("${likeIcons}");
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    background-clip: content-box;
    background-color: blue;
  }
  ${(p) => p.styles}
`;

const ArticleStatistic = ({ statistic, styles }) => {
  return (
    <ArticleStatisticStyle styles={styles}>
      <motion.div className="likes" whileHover={{ backgroundColor: "#f8f8ff" }}>
        <div className="likeIcon" />
        <span>{statistic || Math.floor(Math.random() * 500)} </span>
      </motion.div>
      <div className="comments">
        <div className="commentIcon" />
        <span>{statistic || Math.floor(Math.random() * 100)} </span>
      </div>
      <div className="views">
        <div className="viewIcon" />
        <span>{statistic || Math.floor(Math.random() * 1000)} </span>
      </div>
    </ArticleStatisticStyle>
  );
};

export default ArticleStatistic;
