import styled from "styled-components";
import { motion } from "framer-motion";

const TitleComponentStyle = styled(motion.div)`
  position: relative;
  height: auto;
  width: 90vw;
  margin: 0px 5vw 0 5vw;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: flex-end;
  background-color: ${(p) => p.title};
  .title {
    font-size: 50px;
    color: ${(p) => p.theme.color};
    white-space: nowrap;
  }
  .subTitle {
    width: 40%;
    font-size: 18px;
    //  color: ${(p) => p.subtitle};
  }
`;

const TitleComponent = ({
  title = { color: "#f8f8ff", text: "some title" },
  subtitle = { color: "#F2C14E", text: "some sub title" },
}) => (
  <TitleComponentStyle theme={title.color}>
    <div className="title">{title.text}</div>
    <div className="subTitle">{subtitle.text}</div>
  </TitleComponentStyle>
);

export default TitleComponent;
