import { motion } from "framer-motion";
import { Link, useLocation } from "react-router-dom";

const HomeNavbarDown = [
  "Archive Page",
  "Author Page",
  "Author Page 2",
  "Search Page",
  "articles Template",
  "articles Template 2",
];
const Paths = [
  "/topics",
  "/author",
  "/author2",
  "/search",
  "/article",
  "/article2",
];

const NavbarTemplates = ({ controller }) => {
  const controllers = {
    0: useAnimation(),
    1: useAnimation(),
    2: useAnimation(),
  };
  const location = useLocation().pathname;

  const isHere = (e) => {
    e = Paths[e];
    if (e === location) return true;
    else return false;
  };

  Paths.forEach((e, i) => {
    console.log(location);
    if (e === location) controllers[i].start({ color: "#f2c14e" });
  });

  return (
    <motion.div
      className="dropDown"
      animate={controller}
      whileHover={{ opacity: 1 }}
    >
      {HomeNavbarDown.map((e, i) => (
        <Link to={Paths[i]} className="dropDownItem" key={i}>
          <motion.span
            className="span"
            whileHover={{ color: "#f2c14e" }}
            transition={{ duration: 0.2 }}
          >
            {e}
          </motion.span>
        </Link>
      ))}
    </motion.div>
  );
};
export default NavbarTemplates;
