import styled from "styled-components";
import { motion } from "framer-motion";

const AuthorstListStyle = styled(motion.div)`
  position: relative;
  margin-top: 200px;
  width: 93vw;
  margin-left: 5vw;
  background-color: lightblue;
  height: auto;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  .Author {
    position: relative;
    margin-right: 50px;
    margin-top: 50px;
    width: 200px;
    height: 200px;
    background-color: #f8f8ff;
    .AuthorBg {
      position: absolute;
      top: 0px;
      height: 110px;
      width: 200px;
      background-color: green;
    }
    .authorDeatils {
      padding: 20px;
      z-index: 100;
      opacity: 0.8;
      position: absolute;
      bottom: 00px;
      height: 100px;
      width: 160px;
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      font-size: 16px;
      color: wheat;
      color: #53504d;
      .AuthorPIc {
        height: 50px;
        width: 50px;
        background-color: whitesmoke;
        border-radius: 100px;
      }
    }
  }
`;

const AuthorsList = () => {
  return (
    <AuthorstListStyle>
      {[1, 1, 1, 1, 1, 1, 1, 1, 1].map((e, i) => {
        return (
          <div className="Author">
            <div className="AuthorBg" />
            <div className="authorDeatils">
              <div className="AuthorPIc" />
              <span className="name">tahar belghitri</span>
              <span className="posts"> 201 posts </span>
              <span className="startedAt"> started at 02/02/2021 </span>
            </div>
          </div>
        );
      })}
    </AuthorstListStyle>
  );
};

export default AuthorsList;
