import styled from "styled-components";
import { motion } from "framer-motion";
import ArticleStatistic from "./articleStatisti.component";

const CommentsComponentStyle = styled(motion.div)`
  background-color: darkred;
  position: relative;
  display: flex;
  flex-direction: column;
  border-left: 1px solid #f8f8ff;
  padding-left: 50px;
  width: 800px;
  padding-top: 50px;
  .commentInfo {
    margin-bottom: 20px;
    height: 50px;
    display: flex;
    flex-direction: row;
    align-items: center;
    .userName {
      color: #f2c14e;
      font-size: 24px;
      margin-left: 20px;
    }
    .userPic {
      height: 50px;
      width: 50px;
      background-color: chocolate;
      border-radius: 100%;
    }
    .more {
      position: absolute;
      right: 0;
      border-radius: 100%;
      float: right;
      height: 50px;
      width: 50px;
      background-color: burlywood;
    }
  }
  .content {
    font-size: 24px;
    color: #f8f8ff;
  }
  .replay {
    position: relative;
    margin-left: 0px;
    height: auto;
    width: 500px;
    padding-top: 50px;
  }
  .load {
    width: 130px;
    margin-top: 20px;
    cursor: pointer;
    font-size: 20px;
    color: #f8f8ff;
  }
  @media (max-width: 1000px) {
    width: 90vw;
    /* width: 0;
    background-color: darkred;
    * {
      width: 0;
    } */
    .replay {
      /* position: relative;
      background-color: darkmagenta;
      width: 00vw;
      * {
        width: 0;
      } */
      /* .more {
        right: 00vw;
      }
      .content {
        width: 60vw;
      }
      .commentInfo {
        background-color: darkorchid;
        width: 50vw;
      } */
    }
  }
  @media (min-width: 2000px) {
    margin-left: calc(50vw - 1000px);
  }
`;

const CommentsComponent = ({ array = [1, 1, 1] }) => {
  return (
    <CommentsComponentStyle>
      <div className="commentInfo">
        <div className="userPic" />
        <span className="userName">tahar belghitri</span>
        <div className="more" />
      </div>
      <span className="content">
        Praesent id massa id nisl venenatis lacinia. Aenean sit amet justo.
        Morbi ut odio.
      </span>
      <ArticleStatistic
        styles={
          ".views { display : none }  margin-top : 30px ; background-color: #f1f1f1; "
        }
      />
      <span className="load">load more +{Math.floor(Math.random() * 20)}</span>
      <div className="replay">
        {array.map((e, i) => {
          return (
            <CommentsComponentStyle key={i}>
              <div className="commentInfo">
                <div className="userPic" />
                <span className="userName">tahar belghitri</span>
                <div className="more" />
              </div>
              <span className="content">
                Praesent id massa id nisl venenatis lacinia. Aenean sit amet
                justo. Morbi ut odio.
              </span>
              <ArticleStatistic
                styles={".views { display : none }  margin-top : 5vw ;"}
              />
            </CommentsComponentStyle>
          );
        })}
      </div>
    </CommentsComponentStyle>
  );
};
export default CommentsComponent;
