import styled from "styled-components";
import { motion, useAnimation } from "framer-motion";
import DashbordDashbord from "./dashbord.dashbord.component";
import DashbordPost from "./dashbord.posts.component";

const DshbordComponenetStyle = styled(motion.div)`
  display: flex;
  flex-direction: column;
  position: relative;
  height: auto;
  left: 5vw;
  width: 90vw;
  margin-top: 150px;
  .tags {
    position: relative;
    .tag {
      color: #8a8888;
      padding: 10px;
      margin: 10px;
      cursor: pointer;
    }
    .tag:first-child {
      margin-left: 0;
    }
  }
  .view {
    margin-top: 50px;
  }
  .viewModel {
    position: absolute;
    top: 0;
    margin-top: 50px;
    left: 0;
    opacity: 0;
    height: 100px;
    width: 200px;
    font-size: 50px;
    color: greenyellow;
    cursor: pointer;
  }
`;

const DashbordComponenet = () => {
  let selected = 0;

  let AnimatDashbord = useAnimation();

  let controllers = {
    0: useAnimation(),
    1: useAnimation(),
    2: useAnimation(),
    3: useAnimation(),
    4: useAnimation(),
    5: useAnimation(),
  };
  const TestView = ({ tag, controllers }) => (
    <motion.span
      className={"viewModel " + tag.replace(" ", "_")}
      animate={controllers}
    >
      {tag.replace(" ", "_")}
    </motion.span>
  );

  return (
    <DshbordComponenetStyle animate={AnimatDashbord}>
      <div className="tags">
        {[
          "Dash board",
          "Posts",
          "Edit profile",
          "Subscription",
          "Billing address",
          "Submit post",
        ].map((e, i) => {
          return (
            <motion.span
              whileHover={{ color: "#f8f8f8" }}
              className="tag"
              onClick={async () => {
                if (selected !== i) {
                  await controllers[selected].start({ opacity: 0 });
                  await AnimatDashbord.start({ height: "1000px" });
                  await controllers[i].start({ opacity: 1 });
                  selected = i;
                }
                console.log(e);
              }}
            >
              {e}
            </motion.span>
          );
        })}
      </div>
      <div className="view">
        <DashbordDashbord controller={controllers[0]} />
        <DashbordPost />
        {/* <TestView tag="Subscription" controllers={controllers[3]} />
        <TestView tag="Posts" controllers={controllers[1]} />
        <TestView tag="Edit profile" controllers={controllers[2]} />{" "}
        <TestView tag="Dash board" controllers={controllers[0]} /> */}
      </div>
    </DshbordComponenetStyle>
  );
};

export default DashbordComponenet;
