import styled from "styled-components";
import { motion } from "framer-motion";

const AuthorHomeComponentsStyle2 = styled(motion.div)`
  position: relative;
  margin-top: 100px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  width: 90%;
  left: 5%;
  height: 800px;
  max-width: 1000px;

  .authorCover {
    position: absolute;
    top: 0;
    width: 100%;
    height: 400px;
    background-color: lightsalmon;
  }
  .uauthorDetails {
    z-index: 20;
    position: absolute;
    top: 0;
    margin-top: 350px;
    width: 45%;
    height: 400px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    .author {
      display: flex;
      align-items: baseline;
    }
    .authorPic {
      height: 95px;
      width: 95px;
      border: 5px #f8f8ff solid;
      background-image: url("https://images.pexels.com/photos/91227/pexels-photo-91227.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940");
      background-position: center;
      background-repeat: no-repeat;
      background-size: cover;
      background-clip: border-box;
      border-radius: 100%;
    }
    .name {
      font-size: 32px;
      color: #f8f8ff;
    }
    .socialMedia {
      height: 50px;
      width: 225px;
      display: flex;
      justify-content: space-between;
      div {
        height: inherit;
        width: 50px;
        background-color: ivory;
        border-radius: 100%;
      }
    }
    .descirption {
      font-size: 20px;
      color: #c1c0c4;
    }
  }
  @media (max-width: 1000px) {
    width: 90vw;
    height: 70vw;
    .authorPic {
      min-height: 20vw;
      min-width: 20vw;
      max-height: 20vw;
      max-width: 20vw;
    }
    .authorCover {
      height: 30vw;
    }
    .socialMedia {
      width: 90vw;
    }
    .uauthorDetails {
      width: 90vw;
      height: 70vw;
      max-height: 350px;
      margin-top: 20vw;
      .name {
        font-size: 5vw;
      }
      .descirption {
        font-size: 16px;
      }
      .socialMedia {
        height: 10vw;
        div {
          max-height: 40px;
          max-width: 40px;
          height: inherit;
          width: 10vw;
        }
      }
    }
  }
  @media (min-width: 2000px) {
    width: 1850px;
    margin-left: calc(50vw - 1000px);
  }
`;
const AuthorHomeComponents2 = () => {
  return (
    <AuthorHomeComponentsStyle2>
      <div className="uauthorDetails">
        <div className="author">
          <div className="authorPic" />
          <span className="name"> tahar belghitri </span>
        </div>
        <div className="socialMedia">
          <div></div>
          <div></div>
          <div></div>
        </div>
        <span className="descirption">
          There’s no stopping the tech giant. Apple now opens its 100th store in
          China.There’s no stopping the tech giant.
        </span>
      </div>
      <div className="authorCover" />
    </AuthorHomeComponentsStyle2>
  );
};

export default AuthorHomeComponents2;
