import styled from "styled-components";
import { motion } from "framer-motion";

const AuthorComponentStyle = styled(motion.div)`
  position: relative;
  display: flex;
  flex-direction: row;
  width: auto;
  .authorBg {
    height: 50px;
    width: 50px;
    background-image: url("http://fress.co/wp-content/uploads/2014/08/sitios-naturales-mas-peligrosos-del-mundo-10.jpg");
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    background-clip: border-box;
  }
  .authorDetails {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
  }
`;

const AuthorComponent = ({
  author = { name: "tahar belghitri", startedAt: "02/02/2021" },
}) => {
  return (
    <AuthorComponentStyle>
      <div className="authorPIc" />
      <div className="authorDetails">
        <span className="name">{author.name}</span>
        <span className="startedAt"> {author.startedAt} </span>
      </div>
    </AuthorComponentStyle>
  );
};
export default AuthorComponent;
