import styled from "styled-components";
import { motion } from "framer-motion";
import AuthorComponent from "./author.component";
import ArticleStatistic from "./articleStatisti.component";

const ReadArticleComponentStyle = styled(motion.div)`
  position: relative;
  width: 90vw;
  left: 5vw;
  height: auto;
  margin-top: 100px;
  display: flex;
  flex-direction: column;
  justify-content: start;
  .articleTag {
    padding: 10px;
    background-color: #f2c14e;
    color: #111111;
    width: 50px;
    margin-bottom: 50px;
  }
  .title {
    font-size: 50px;
    color: #f8f8ff;
    width: 750px;
    margin-bottom: 50px;
  }
  .details {
    margin-top: 30px;
    display: flex;
    flex-direction: row;
  }
`;
const ReadArticleComponent = () => {
  return (
    <ReadArticleComponentStyle>
      <div className="articleTag">travel</div>
      <div className="title">
        the bad habits you should avoid in the big cities
      </div>
      <ArticleStatistic />
      <div className="details">
        <AuthorComponent />
        <div className="more" />
      </div>
      <div className="articleCover" />
    </ReadArticleComponentStyle>
  );
};
export default ReadArticleComponent;
