import { motion } from "framer-motion";
import styled from "styled-components";

const Home2Style = styled(motion.div)`
  position: relative;
  top: 8vw;
  left: 0;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  width: 100vw;
  padding: 40px;
  font-size: 16px;
  background-image: url("https://images.pexels.com/photos/3304170/pexels-photo-3304170.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940");
  background-attachment: fixed;
  background-position: 0 -50vw;
  background-repeat: no-repeat;
  background-size: cover;
  background-clip: border-box;
  height: calc(45vw + 80px);
  max-height: 800px;
  .aboutUs {
    height: 100px;
    width: 500px;
    position: absolute;
    bottom: 0;
    margin-bottom: 40px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    color: #f8f8ff;
  }
  .intro {
    display: flex;
    flex-direction: column;
    height: auto;
    .logo {
      margin-top: 30px;
      font-size: 100px;
      margin-bottom: 30px;
      color: #f8f8ff;
    }
    .welcome,
    .text {
      color: #f8f8ff;
      width: 300px;
    }
  }
  @media (min-width: 1450px) {
    background-position: 0 -60vw;
  }
  @media (min-width: 1800px) {
    background-position: 0 -60vw;
  }
  @media (min-width: 1500px) {
    background-position: 0 -60vw;
  }
  @media (max-width: 1000px) {
    background-position: 0 -20vw;
    height: 80vw;
  }
  @media (max-width: 750px) {
    padding: 10px;
    width: calc(100%);
    background-position: 0 -50vw;
    margin: 0;
    margin-top: 10vw;
    height: 100vw;
    .intro {
      .welcome {
        width: 100%;
        width: auto;
        white-space: pre-wrap;
      }
      .logo {
        font-size: 20vw;
      }
      .text {
        width: auto;
        white-space: pre-wrap;
      }
    }

    .aboutUs {
      white-space: pre-wrap;
    }
  }
  @media (max-width: 520px) {
    margin-top: 12vw;
    padding-left: 5vw;
    background-position: -50vw -110vw;
    width: 100vw;
    height: 120vw;
    .aboutUs {
      * {
        margin-bottom: 2vw;
      }
      width: 80%;
    }
  }
  @media (min-width: 2000px) {
    width: calc(2000px - 5vw);
    margin-left: calc(55vw - 1000px);
    background-position: 0px -1800px;
  }
`;

