import styled from "styled-components";
import { motion } from "framer-motion";

const EditProfileComponentStyle = styled(motion.div)`
  position: absolute;
  margin-top: 00px;
  height: 0;
  width: 100%;
  opacity: 0;
  overflow: hidden;
  .profileDetails {
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
    div {
      display: flex;
      flex-direction: column;
      margin: 20px;
      margin-bottom: 50px;
      width: 40vw;
      margin-left: 20px;
      span {
        font-size: 20px;
        color: #f2c14e;
      }
      input {
        padding: 10px;
        border: none;
        outline: none;
        background-color: transparent;
        border-bottom: 2px solid #8a8888;
        color: #f8f8ff;
        font-size: 20px;
      }
      input :placeholder {
        font-size: 16px;
      }
      input :focus {
        border: none;
        outline: none;
      }
    }
  }
  .email {
    display: flex;
    flex-direction: column;
    margin: 20px;
    margin-bottom: 50px;
    width: 40vw;
    margin-left: 20px;
    span {
      font-size: 20px;
      color: #f2c14e;
    }
    input {
      padding: 10px;
      border: none;
      outline: none;
      background-color: transparent;
      border-bottom: 2px solid #8a8888;
      color: #f8f8ff;
      font-size: 20px;
    }
    input :placeholder {
      font-size: 16px;
    }
    input :focus {
      border: none;
      outline: none;
    }
  }
`;
const DashbordEditProfileComponent = ({ controller }) => {
  return (
    <EditProfileComponentStyle animate={controller}>
      <div className="profileDetails">
        <div>
          <span> First name </span>
          <input placeholder="first name" />
        </div>
        <div>
          <span> Last name </span>
          <input placeholder="last name" />
        </div>
        <div>
          <span> Current password </span>
          <input placeholder="current password" />
        </div>
        <div>
          <span> New password </span>
          <input placeholder="new password" />
        </div>
      </div>
      <div className="email">
        <span> Email Address </span>
        <input placeholder="email" />
      </div>
    </EditProfileComponentStyle>
  );
};
export default DashbordEditProfileComponent;
