import styled from "styled-components";
import { motion } from "framer-motion";

const AuthorHomeComponentsStyle2 = styled(motion.div)`
  position: relative;
  margin-top: 100px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  width: 90%;
  left: 5%;
  height: 800px;
  max-width: 1000px;
  .authorCover {
    position: absolute;
    top: 0;
    width: 45%;
    height: 400px;
    background-color: lightsalmon;
  }
  .uauthorDetails {
    z-index: 20;
    position: absolute;
    top: 0;
    margin-top: 350px;
    width: 45%;
    height: 400px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    .authorPic {
      height: 100px;
      width: 100px;
      background-color: lightslategrey;
      border-radius: 100%;
    }
    .name {
      font-size: 50px;
      color: #f8f8ff;
    }
    .socialMedia {
      height: 50px;
      width: 225px;
      display: flex;
      justify-content: space-between;
      div {
        height: inherit;
        width: 50px;
        background-color: ivory;
        border-radius: 100%;
      }
    }
    .descirption {
      font-size: 20px;
      color: #c1c0c4;
    }
  }
`;
const AuthorHomeComponents2 = () => {
  return (
    <AuthorHomeComponentsStyle2>
      <div className="uauthorDetails">
        <div className="authorPic" />
        <span className="name"> tahar belghitri </span>
        <div className="socialMedia">
          <div></div>
          <div></div>
          <div></div>
        </div>
        <span className="descirption">
          There’s no stopping the tech giant. Apple now opens its 100th store in
          China.There’s no stopping the tech giant.
        </span>
      </div>
      <div className="authorCover" />
    </AuthorHomeComponentsStyle2>
  );
};

export default AuthorHomeComponents2;
