import styled from "styled-components";
import { motion } from "framer-motion";
import ArticleStatistic from "./articleStatisti.component";

const CommentsComponentStyle = styled(motion.div)`
  position: relative;
  .userPic {
    position: absolute;
    top: 0;
  }
`;

const CommentsComponent = () => {
  return (
    <CommentsComponentStyle>
      <div className="comment">
        <div className="userPic" />
        <div className="userDetails">
          <span className="userName">tahar belghitri</span>
          <div className="more" />
        </div>
        <span className="content">
          Praesent id massa id nisl venenatis lacinia. Aenean sit amet justo.
          Morbi ut odio.
        </span>
        <ArticleStatistic
          styles={".views { display : none }  margin-top : 30px ;"}
        />
      </div>
    </CommentsComponentStyle>
  );
};
export default CommentsComponent;
