import styled from "styled-components";
import { motion, useAnimation } from "framer-motion";
import { Link } from "react-router-dom";

const NavbarHorizontalStyle = styled(motion.div)`
  width: 100vw;
  height: 10vw;
  z-index: 20000;
  position: fixed;
  top: 0;
  left: 0;
  display: none;
  background-color: #191919;
  .icon {
    position: absolute;
    height: 7vw;
    width: 7vw;
    top: 1.5vw;
    right: 5vw;
    background-color: green;
    border-radius: 100%;
    z-index: 10001;
    cursor: pointer;
  }
  .logo {
    position: absolute;
    display: flex;
    left: 5vw;
    justify-content: center;
    align-items: center;
    height: 100%;
    color: #f2c14e;
    cursor: pointer;
    display: flex;
    font-size: 5vw;
  }
  .navbar {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    padding-top: 0vw;
    padding-left: 5vw;
    height: 110vh;
    padding-bottom: 20vw;
    background-color: rgba(25, 25, 25, 1);
    z-index: -100000;
    display: flex;
    flex-direction: column;
    color: #f8f8ff;
    overflow: auto;
    opacity: 0;
    display: none;
    .elmnt {
      margin-top: 30px;
      display: flex;
      flex-direction: column;
      .content {
        display: flex;
        .mainLinks {
          text-decoration: none;
          cursor: pointer;
          color: #f2c14e;
          font-size: 30px;
        }
      }
      .downItem {
        height: auto;
        width: auto;
        margin-left: 5vw;
        font-size: 20px;
        display: flex;
        flex-direction: column;
        height: auto;
        display: none;
        opacity: 0;
        div {
          margin-top: 20px;
        }
        .dropDownTitle {
          margin-top: 20px;
          color: #f8f8ff;
          font-size: 30px;
          text-decoration: none;
        }
      }
      .downIcon {
        cursor: pointer;
        margin-left: 50px;
        height: 30px;
        width: 30px;
        background-color: sandybrown;
        border-radius: 100%;
      }
      .name {
        cursor: pointer;
        color: #f2c14e;
        font-size: 30px;
      }
    }
  }
  @media (min-width: 800px) {
    height: 100px;
    .icon {
      height: 50px;
      width: 50px;
      top: 25px;
      background-color: lavender;
    }
    .logo {
      font-size: 40px;
    }
  }
  @media (max-width: 1100px) {
    display: block;
  }
`;

const NavbarContent = [
  "home",
  "Five cols",
  "Fewer cols",
  "Templates",
  "Other pages",
];

const Home = ["demo style 1", "demo style 2", "demo style 3"];
const HomePaths = ["/", "home2", "home3"];
const Templates = [
  "About",
  "Contact us",
  "Login",
  "Signup",
  "Forgot Password",
  "Dashboard",
];
const TemplatePaths = ["/aboute", "/contactus", "/", "/", "/", "/dashboard"];

const OtherPages = [
  "Archive Page",
  "Author Page",
  "Search Page",
  "articles Template",
];
const OtherPagesPaths = ["/topics", "/author", "/search", "/article"];

const DownElmnts = {
  home: [Home, HomePaths],
  "Five cols": [Home],
  "Fewer cols": [Home],
  Templates: [OtherPages, TemplatePaths],
  "Other pages": [Templates, OtherPagesPaths],
};

const MainPaths = ["/", "/", "/", "/aboute", "/topics"];

const DropDownComponent = (name, controller) => {
  const elemnt = DownElmnts[name];
  if (elemnt.length > 1) {
    return elemnt[0].map((e, i) => {
      return (
        <div key={i}>
          <Link
            to={elemnt[1][i]}
            className="dropDownTitle"
            key={i}
            onClick={async () => {
              controller.start({ opacity: 0, zIndex: 100000 });
            }}
          >
            {e}
          </Link>
        </div>
      );
    });
  }
};

const NavbarHorizontal = () => {
  const ShowNavbar = useAnimation();
  let IsNavbarVisible = false;

  const controllers = {
    0: useAnimation(),
    1: useAnimation(),
    2: useAnimation(),
    3: useAnimation(),
    4: useAnimation(),
  };
  const controllers2 = {
    0: useAnimation(),
    1: useAnimation(),
    2: useAnimation(),
    3: useAnimation(),
    4: useAnimation(),
  };
  let isShowed = {
    0: false,
    1: false,
    2: false,
    3: false,
    4: false,
    5: false,
  };
  return (
    <NavbarHorizontalStyle>
      <motion.div
        className="icon"
        transition={{
          duration: 0.01,
        }}
        onClick={() => {
          if (IsNavbarVisible) ShowNavbar.start({ opacity: 0, zIndex: -1000 });
          else ShowNavbar.start({ opacity: 1, zIndex: 10000 });
          IsNavbarVisible = !IsNavbarVisible;
        }}
      />
      <span className="logo"> DeepEnd </span>
      <motion.div className="navbar" animate={ShowNavbar}>
        {Object.keys(DownElmnts).map((e, i) => {
          return (
            <div className="elmnt" key={i}>
              <div className="content">
                <span className="name">
                  <Link
                    to={MainPaths[i]}
                    className="mainLinks"
                    onClick={() => {
                      ShowNavbar.start({ display: "block" });
                      ShowNavbar.start({ opacity: 0, zIndex: -1000 });
                    }}
                  >
                    {e}
                  </Link>
                </span>
                <motion.div
                  animate={controllers2[i]}
                  onClick={async () => {
                    console.log(i);
                    if (isShowed[i] === false) {
                      console.log("hereee");
                      await controllers[i].start({
                        height: "auto",
                        opacity: 1,
                      });
                      await controllers[i].start({
                        display: "block",
                      });
                      isShowed[i] = true;
                    } else {
                      isShowed[i] = false;
                      await controllers[i].start({
                        opacity: 0,
                      });
                      await controllers[i].start({
                        height: "0px",
                      });
                      await controllers[i].start({
                        display: "none",
                      });
                    }
                  }}
                  className={DownElmnts[e].length > 1 ? "downIcon" : ""}
                />
              </div>
              <motion.div className="downItem" animate={controllers[i]}>
                {DropDownComponent(e, ShowNavbar)}
              </motion.div>
            </div>
          );
        })}
      </motion.div>
    </NavbarHorizontalStyle>
  );
};

export default NavbarHorizontal;
