import styled from "styled-components";
import { motion } from "framer-motion";
import ArticleListComponent from "../components/articeList.component";
import TitleComponent from "../components/title.component";
import ArticleComponent from "../components/article.component";
import TagLIstComponent from "../components/tagList.component";

const TagLIstComponentStyle1 = styled(motion.div)`
  width: auto;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  font-size: 16px;
  background-color: yellowgreen;
  margin-top: 20px;

  .tag {
    width: auto;
    height: auto;
    padding: 10px;
    margin-left: 20px;
  }
`;

const TagLIstComponent1 = ({ list = [] }) => {
  return (
    <TagLIstComponentStyle1>
      {(list || ["health", "traveling", "media", "news", "psychologie"]).map(
        (e, i) => {
          return (
            <motion.span
              whileHover={{ backgroundColor: "white", color: "black" }}
              className={"tag tag" + e}
            >
              {e}
            </motion.span>
          );
        }
      )}
    </TagLIstComponentStyle1>
  );
};

const LatestArticlesLayoutStyle = styled(motion.div)`
  position: relative;
  height: auto;
  width: 90vw;
  margin-left: 5vw;
`;

const LatestArticlesLayout = () => {
  return (
    <>
      <TitleComponent
        styles={`width : 300px`}
        text={{
          title: "Latest articles",
          subTitle:
            "Discover the most outstanding articles in all topics of life.",
        }}
        colors={{ title: "#F8F8FF", subTitle: "#8A8888" }}
      />
      <LatestArticlesLayoutStyle>
        <TagLIstComponent1 />
        <ArticleListComponent />
        <ArticleComponent />
      </LatestArticlesLayoutStyle>
    </>
  );
};
export default LatestArticlesLayout;
