import styled from "styled-components";
import { motion } from "framer-motion";
import ArticleStatistic from "./articleStatisti.component";
import AuthorComponent from "./author.component";
import { useHistory } from "react-router";
import { articles } from "../authorsData.json";

const ArticleComponentStyle = styled(motion.div)`
  display: flex;
  flex-direction: column;
  position: relative;
  padding: 0;
  width: auto;
  max-width: 400px;
  .articleType {
    position: absolute;
    top: 0;
    padding: 10px;
    background-color: #53504d;
    color: #f8f8ff;
    cursor: pointer;
    max-width: 400px;
  }
  .ArticleCover {
    width: 40vw;
    max-width: 400px;
    height: 20vh;
    background-image: url("http://fress.co/wp-content/uploads/2014/08/sitios-naturales-mas-peligrosos-del-mundo-10.jpg");
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    background-clip: border-box;
    margin-bottom: 20px;
    cursor: pointer;
  }
  .ArticleTitle {
    font-size: 24px;
    color: #f8f8ff;
    margin-bottom: 10px;
    cursor: pointer;
    width: 30vw;
    max-width: 400px;
  }
  .subTitle {
    font-size: 16px;
    max-width: 400px;
    color: #c1c0c4;
    margin-bottom: 20px;
    cursor: pointer;
  }
  ${(p) => p.styles}
  @media(max-width : 1000px) {
    margin-right: 5vw;
    width: 90vw;
    .ArticleTitle {
      width: 90vw;
      max-width: none;
    }
    .ArticleCover {
      width: 90vw;
      height: 30vw;
      max-width: none;
    }
  }
`;

const ArticleComponent = ({ styles = "" }) => {
  const history = useHistory();
  return articles[Math.floor(Math.random() * 12)].map((e, i) => {
    {
      <ArticleComponentStyle
        styles={styles}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5, delay: 0.9 }}
        onClick={() => history.push("/article")}
      >
        <div className="articleType"> travele </div>
        <div className="ArticleCover" />
        <span className="ArticleTitle">
          The most beautiful and dangerous places in the world.
        </span>
        <span className="subTitle">
          the most beutiful and dangeruis places in the world the most beutiful
          and dangeruis places in the world the most beutiful and dangeruis
          places in the world
        </span>
        <ArticleStatistic />
        <AuthorComponent />
      </ArticleComponentStyle>;
    }
  });
};
export default ArticleComponent;
