import styled from "styled-components";
import { motion } from "framer-motion";
import ArticleStatistic from "./articleStatisti.component";
import AuthorComponent from "./author.component";

const SlideComponentStyle = styled(motion.div)`
  display: flex;
  flex-direction: row;
  width: 100%;
  height: 150px;
  padding: 20px;
  padding-top: 0;
  padding-right: 0;
  font-size: 16px;
  margin-bottom: 50px;
  .articleCover {
    height: 150px;
    width: 400px;
    margin-right: 20px;
    background-image: url("http://fress.co/wp-content/uploads/2014/08/sitios-naturales-mas-peligrosos-del-mundo-10.jpg");
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    background-clip: border-box;
  }
  .ArticleDetails {
    position: relative;
    height: 130px;
    width: 390px;
    display: flex;
    justify-content: flex-end;
    flex-direction: column;
    .title {
      color: #dddce2;
      position: absolute;
      top: 0;
      font-size: 18px;
    }
  }
  @media (max-width: 1860px) {
    .articleCover {
      width: 250px;
    }
  }
  @media (max-width: 1500px) {
    width: 40vw;
  }

  @media (max-width: 1100px) {
    margin-top: 50px;
    width: 60vw;
    margin-left: 0;
    padding-left: 0;
    &:first-child {
      margin-top: 100px;
    }
  }
  @media (max-width: 800px) {
    .articleCover {
      min-width: 150px;
    }
  }
`;

const SlideComponent = () => {
  return (
    <SlideComponentStyle>
      <div className="articleCover" />
      <div className="ArticleDetails">
        <span className="title">
          the most beutiful and dangeruis places in the world
        </span>
        <span className="subTitle"></span>
        <ArticleStatistic
          styles={`span { font-size : 14px ;}  
        .likeIcon,
        .viewIcon,
        .commentIcon {
            height : 10px ;
            width :10px;
        }
        `}
        />
        <AuthorComponent
          styles={`
            span { font-size : 14px ; }
            .authorPIc {
                height : 30px ;
                width :30px;
            }
            .startedAt{
                display : none;
            }
        `}
        />
      </div>
    </SlideComponentStyle>
  );
};

export default SlideComponent;
