import { motion } from "framer-motion";
import { Link } from "react-router-dom";
import { useAnimation } from "framer-motion";
const HomeNavbarDown = [
  "About",
  "Contact us",
  "Login",
  "Signup",
  "Forgot Password",
  "Dashboard",
];
const Paths = ["/aboute", "/contactus", "/login", "/signin", "/", "/dashboard"];

const NavbarOtherPages = ({ controller }) => {
  const controllers = {
    0: useAnimation(),
    1: useAnimation(),
    2: useAnimation(),
    3: useAnimation(),
    4: useAnimation(),
    5: useAnimation(),
  };
  const location = useLocation().pathname;

  const isHere = (e) => {
    e = Paths[e];
    if (e === location) return true;
    else return false;
  };

  Paths.forEach((e, i) => {
    console.log(location);
    if (e === location) controllers[i].start({ color: "#f2c14e" });
  });

  return (
    <motion.div
      className="dropDown"
      animate={controller}
      whileHover={{ opacity: 1 }}
    >
      {HomeNavbarDown.map((e, i) => (
        <Link to={Paths[i]} className="dropDownItem" key={i}>
          <motion.span
            className="span"
            whileHover={{ color: "#f2c14e" }}
            transition={{ duration: 0.2 }}
          >
            {e}
          </motion.span>
        </Link>
      ))}
    </motion.div>
  );
};
export default NavbarOtherPages;
