import styled from "styled-components";
import { motion } from "framer-motion";

const AuthorComponentStyle = styled(motion.div)`
  position: relative;
  display: flex;
  flex-direction: row;
  width: 200px;
  height: 40px;
  margin-top: 10px;
  .authorPIc {
    height: 40px;
    width: 40px;
    background-image: url("https://images.pexels.com/photos/91227/pexels-photo-91227.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940");
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    background-clip: border-box;
    border-radius: 100%;
    margin-right: 10px;
  }
  .authorDetails {
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    .name {
      font-size: 16px;
      color: #f8f8ff;
    }
    .startedAt {
      font-size: 16px;
      color: #8a8888;
    }
  }
  ${(p) => p.styles}
  span {
    color: #111111;
  }
`;

const AuthorComponent = ({
  author = { name: "tahar belghitri", startedAt: "02/02/2021" },
  styles = "",
}) => {
  return (
    <AuthorComponentStyle styles={styles}>
      <div className="authorPIc" />
      <div className="authorDetails">
        <span className="name">{author.name}</span>
        <span className="startedAt"> {author.startedAt} </span>
      </div>
    </AuthorComponentStyle>
  );
};
export default AuthorComponent;
