import styled from "styled-components";
import { motion } from "framer-motion";
import AuthorComponent from "./author.component";
import ArticleStatistic from "./articleStatisti.component";
import moreIcon from "../icons/more-icon.png";

const ReadArticleComponentStyle = styled(motion.div)`
  position: relative;
  margin-top: 12vw;
  width: 90vw;
  left: 5vw;
  height: auto;
  display: flex;
  flex-direction: column;
  justify-content: start;
  .articleTag {
    margin-bottom: 50px;
    span {
      padding: 10px;
      background-color: #f2c14e;
      color: #111111;
    }
  }
  .title {
    font-size: 50px;
    color: #f8f8ff;
    width: 800px;
    margin-bottom: 50px;
  }
  .articleCover {
    margin-top: 100px;
    height: 50vw;
    max-height: 500px;
    width: 88vw;
    background-color: #f2c14e;
    max-width: 1500px;
  }
  .subTitle {
    color: #8a8888;
    margin-top: 50px;
    font-size: 18px;
    width: 800px;
  }
  .details {
    margin-top: 50px;
    display: flex;
    flex-direction: row;
    width: 825px;
    justify-content: space-between;
    .more {
      height: 20px;
      width: 20px;
      padding: 1vw;
      cursor: pointer;
      background-image: url(${moreIcon});
      background-position: center;
      background-repeat: no-repeat;
      background-size: cover;
      background-clip: border-box;
    }
  }
  @media (max-width: 1100px) {
    .title {
      font-size: 30px;
      width: 90vw;
    }
    .subTitle {
      font-size: 16px;
      width: 90vw;
    }
    .details {
      width: 90vw;
    }
  }
  @media (min-width: 2000px) {
    width: 1850px;
    margin-left: calc(50vw - 1000px);
    .articleCover {
      width: 1500px;
    }
  }
`;
const ReadArticleComponent = () => {
  return (
    <ReadArticleComponentStyle
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5, delay: 0.3 }}
      exit={{ opacity: 0 }}
    >
      {window.scrollTo(0, 0)}
      <div className="articleTag">
        <span>travel</span>
      </div>
      <div className="title">
        The bad habits you should avoid in the big cities.
      </div>
      <ArticleStatistic />
      <div className="details">
        <AuthorComponent />
        <div className="more" />
      </div>
      <span className="subTitle">
        Weâ€™re an online magazine dedicated to covering the best in international
        product design. We started as a little blog back in 2002 covering
        student work and over time
      </span>
      <div className="articleCover" />
    </ReadArticleComponentStyle>
  );
};
export default ReadArticleComponent;
