import { motion } from "framer-motion";
import styled from "styled-components";

const HomeComponentStyle = styled(motion.div)`
  position: relative;
  left: 5vw;
  margin-top: 100px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  height: 600px;
  width: 90vw;
  font-size: 1.2vw;
  height: auto;
  .introBg {
    background-image: url("https://images.pexels.com/photos/3304170/pexels-photo-3304170.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940");
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    background-clip: border-box;
    width: 40vw;
    height: 50vw;
  }
  .aboutUs {
    height: 100px;
    width: 500px;
    position: absolute;
    bottom: 0;
    display: flex;
    flex-direction: column;
    line-height: 20px;
    color: #c1c0c4;
  }
  .intro {
    display: flex;
    flex-direction: column;
    height: auto;
    .logo {
      margin-top: 30px;
      font-size: 5vw;
      margin-bottom: 30px;
      color: #f8f8ff;
    }
    .welcome,
    .text {
      color: #c1c0c4;
      width: auto;
    }
  }
  @media (max-width: 990px) {
    flex-direction: column;
    .aboutUs {
      position: relative;
      top: 0;
      margin-top: 50px;
    }
    .introBg {
      margin-top: 50px;
      width: 100%;
    }
  }
`;

const HomeComponent = () => {
  return (
    <HomeComponentStyle>
      <div className="intro">
        <span className="welcome"> welcome to </span>
        <span className="logo"> deepend </span>
        <span className="text">
          “We Become What We Think About.” <br /> -Earl Nightingale
        </span>
      </div>
      <div className="aboutUs">
        <span> we gave our visitor what they exactly want to read </span>
        <span>
          simple title with deep content could make them read the articles three
          times or more
        </span>
        <span>
          many articles type because of the good trafic we have and our newest
          authors ebery day
        </span>
      </div>
      <div className="introBg" />
    </HomeComponentStyle>
  );
};
export default HomeComponent;
