import styled from "styled-components";
import { motion } from "framer-motion";

const TitleComponentStyle = styled(motion.div)`
  position: relative;
  height: auto;
  width: 90vw;
  margin: 0px 5vw 0 5vw;
  display: flex;
  flex-direction: row;
  align-items: flex-end;
  .title {
    font-size: 5vw;
    color: ${(props) => props.theme.title};
    white-space: nowrap;
    margin-right: 5vw;
    line-height: 0.7;
    ${(p) => p.titleSize}
  }
  .subTitle {
    width: 40%;
    font-size: 1.2vw;
    color: ${(p) => p.theme.subTitle};
    width: 30vw;
  }
  ${(p) => p.styles};
  /* @media (max-width: 1100px) {
    flex-direction: column;
