  },
}) => (
  <TitleComponentStyle theme={colors} styles={styles} titleSize={titleSize}>
    <div className="title">{text.title}</div>
    <div className="subTitle">{text.subTitle}</div>
  </TitleComponentStyle>
);

export default TitleComponent;
