import styled from "styled-components";
import { motion } from "framer-motion";
import AuthorComponent from "./author.component";

const SideArticleViewStyle = styled(motion.div)`
  padding: 10px;
  padding-top: 50px;
  background-color: #e5e5e5;
  .articles {
    display: flex;
    flex-direction: row;

    padding: 10px;
    .articleCover {
      height: 100px;
      width: 150px;
      background-color: lavender;
    }
    .authorDetails {
      display: flex;
      flex-direction: column;
      justify-content: space-around;
      .title {
        margin-left: 00px;
        font-size: 20px;
        font-size: 24px;
      }
    }
  }
`;

const SideArticleView = () => {
  return (
    <SideArticleViewStyle whileHover={{ backgroundColor: "green" }}>
      <span className="title"> More articles </span>
      <div className="articles">
        <div className="authorDetails">
          <span className="title"> side article title </span>
          <AuthorComponent
            styles=".authorDetails {
                .name {
                font-size: 16px;
                color: #223222;
                }
            }"
          />
        </div>
        <div className="articleCover" />
      </div>
    </SideArticleViewStyle>
  );
};

export default SideArticleView;
