import styled from "styled-components";
import { motion } from "framer-motion";
import ArticleStatistic from "./articleStatisti.component";
import AuthorComponent from "./author.component";

const ReadArticleStyle2 = styled(motion.div)`
  margin-top: 500px;
  width: 90vw;
  .articleCover {
    position: absolute;
    top: 0;
    width: 100%;
    height: 600px;
    background-color: azure;
  }
  .articleTag {
    margin-left: 5vw;
    padding: 10px;
    background-color: #f2c14e;
    color: #111111;
    width: 50px;
    margin-bottom: 50px;
  }
  .title {
    margin-left: 5vw;
    font-size: 50px;
    color: #f8f8ff;
    width: 800px;
    margin-bottom: 50px;
  }

  .subTitle {
    margin-left: 50vw;
    color: #8a8888;
    margin-top: 50px;
    font-size: 18px;
    width: 800px;
  }
  .details {
    margin-left: 5vw;
    margin-top: 50px;
    display: flex;
    flex-direction: row;
    width: 825px;
    justify-content: space-between;
    .more {
      height: 50px;
      width: 50px;
      background-color: aqua;
      border-radius: 100%;
    }
  }
`;
const ReadArticle2 = () => {
  return (
    <ReadArticleStyle2>
      <div className="content"></div>
      <div className="articleCover" />
    </ReadArticleStyle2>
  );
};

export default ReadArticle2;
