import { EventEmitter } from "events";

class event extends EventEmitter {
  isScrollingToBottom(cb) {
    return this.on("scrollToBottom", cb);
  }
  isScrollToTop(cb) {
    return this.on("scrollToTop", cb);
  }
  ditectScrollDirection(e) {
    if (e === "TOP") {
      return this.emit("scrollToBottom");
    } else {
      return this.emit("scrollToTop");
    }
  }
  isWidthChange() {
    this.emit("widthChanged", window.innerWidth);
  }
  onWidthChanges(cb) {
    this.on("widthChanged");
  }
}

const events = new event();
export default events;
