import { motion, useAnimation } from "framer-motion";
import styled from "styled-components";

const NavbarCompopentsStyle = styled(motion.div)`
  position: fixed;
  top: 0;
  height: 50px;
  width: 90vw;
  padding: 0 5vw 0 5vw;
  z-index: 10000;
  font-size: 18px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: #191919;
  .logo {
    width: 60%;
    font-size: 24px;
    color: #f2c14e;
    cursor: pointer;
  }
  span,
  .home1 {
    color: #8a8888;
    cursor: pointer;
  }
  .home1 {
    color: #f8f8ff;
  }
`;

const NavbarContent = [
  "home",
  "Five cols",
  "Fewer cols",
  "Templates",
  "Other pages",
];

const HomeNavbarDown = ["style 1", "style 2", "style 2"];

const DropDownStyle = styled(motion.div)``;

const HomeDropDown = <DropDownStyle> {} </DropDownStyle>;

const NavbarComponent = () => {
  const NavbarMotionController = {
    home: useAnimation(),
    Five_Cols: useAnimation(),
    Fewer_cols: useAnimation(),
    Templates: useAnimation(),
    Other_pages: useAnimation(),
  };
  const NavbarDropDown = {
    home: useAnimation(),
    Five_Cols: useAnimation(),
    Fewer_cols: useAnimation(),
    Templates: useAnimation(),
    Other_pages: useAnimation(),
  };

  let selectedItem = 0;
  NavbarDropDown[Object.keys(NavbarMotionController)[0]].start({
    color: "#f8f8ff",
  });

  return (
    <NavbarCompopentsStyle>
      <span className="logo"> DeepEnd </span>
      {NavbarContent.map((e, i) => {
        return (
          <motion.span
            key={i}
            animate={
              NavbarMotionController[Object.keys(NavbarMotionController)[i]]
            }
            onHoverStart={() => {
              if (selectedItem !== i)
                NavbarMotionController[
                  Object.keys(NavbarMotionController)[i]
                ].start({
                  color: "#f2c14e",
                });
            }}
            onHoverEnd={() => {
              if (selectedItem !== i)
                NavbarMotionController[
                  Object.keys(NavbarMotionController)[i]
                ].start({
                  color: "#8a8888",
                });
            }}
            onClick={() => {
              if (selectedItem !== i) {
                NavbarMotionController[
                  Object.keys(NavbarMotionController)[i]
                ].start({
                  color: "#f8f8ff",
                });

                NavbarMotionController[
                  Object.keys(NavbarMotionController)[selectedItem]
                ].start({ color: "#8a8888" });
                selectedItem = i;
              }
            }}
          >
            {e}
          </motion.span>
        );
      })}
    </NavbarCompopentsStyle>
  );
};

export default NavbarComponent;
