import styled from "styled-components";
import { AnimatePresence, motion, useAnimation } from "framer-motion";
import DashbordDashbord from "./dashbord.dashbord.component";
import DashbordPost from "./dashbord.posts.component";
import DashbordEditProfileComponent from "./dashbord.edit.prodile.component";
import DashbordPackInfo from "./dashbord.pack.info.component";
import { useEffect, useState } from "react";
import DashbordAdressComponent from "./dashbord.address.component";

const DshbordComponenetStyle = styled(motion.div)`
  display: flex;
  flex-direction: column;
  position: relative;
  height: auto;
  left: 5vw;
  width: 90vw;
  margin-top: 150px;
  .tags {
    position: relative;
    @media (max-width: 750px) {
      display: flex;
      flex-direction: column;
    }
    .tag {
      color: #8a8888;
      padding: 10px;
      margin: 10px;
      cursor: pointer;
    }
    .tag:first-child {
      margin-left: 0;
    }
  }
  .view {
    margin-top: 50px;
    height: auto;
    width: 100%;
    background-color: green;
  }
  /* .viewModel {
    position: absolute;
    top: 0;
    margin-top: 50px;
    left: 0;
    opacity: 1;
    height: 100px;
    width: 200px;
    font-size: 50px;
    color: greenyellow;
    cursor: pointer;
  } */
  @media (max-width: 750px) {
    margin-bottom: 400px;
    .tags {
      .tag {
        padding-left: 0;
        margin-left: 0;
      }
    }
  }
  @media (min-width: 2000px) {
    width: 1800px;
    margin-left: calc(50vw - 1000px);
  }
`;

const DashbordComponenet = () => {
  const [view, setView] = useState("home");
  let selected = 0;
  let tagSelected = 0;

  const AnimatDashbord = useAnimation();

  let controllers = {
    0: useAnimation(),
    1: useAnimation(),
    2: useAnimation(),
    3: useAnimation(),
    4: useAnimation(),
    5: useAnimation(),
  };
  let tagsControllers = {
    0: useAnimation(),
    1: useAnimation(),
    2: useAnimation(),
    3: useAnimation(),
    4: useAnimation(),
    5: useAnimation(),
  };

  useEffect(() => {
    const fun = async () => {
      tagsControllers[0].start({ color: "#f8f8f8" });
      await AnimatDashbord.start({ height: "100px" });
      controllers[0].start({ height: "200px", opacity: 1 });
    };
    fun();
  });

  return (
    <DshbordComponenetStyle
      animate={AnimatDashbord}
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5, delay: 0.3 }}
      exit={{ opacity: 0 }}
    >
      <div className="tags">
        {[
          "Dash board",
          "Posts",
          "Edit profile",
          "Subscription",
          "Billing address",
          "Submit post",
        ].map((e, i) => {
          return (
            <motion.span
              key={i}
              animate={tagsControllers[i]}
              className="tag"
              onClick={async () => {
                console.log("here");
                // if (selected !== i) {
                // tagsControllers[i].start({ color: "#f8f8f8" });
                // tagsControllers[tagSelected].start({ color: "#8a8888" });
                // tagSelected = i;
                // controllers[selected].start({ opacity: 0 });
                console.log(i);
                // await AnimatDashbord.start({ height: "1100px" });
                // controllers[1].start({ display: "block" });
                // setTimeout(() => {
                //   console.log("here setTimeout");
                //   controllers[1].start({ display: "none" });
                // }, 2000);
                setView(e);
                // if (i === 1) {
                //   console.log("here");
                //   await AnimatDashbord.start({ height: "1100px" });
                //   console.log("heree 2");
                //   await controllers[1].start({ display: "block" });
                //   controllers[1].start({ height: "1100px", opacity: 1 });
                // } else if (i === 0) {
                //   await AnimatDashbord.start({ height: "100px" });
                //   await controllers[0].start({ display: "block" });
                //   controllers[0].start({ height: "200px", opacity: 1 });
                // } else if (i === 2) {
                //   await AnimatDashbord.start({ height: "800px" });
                //   await controllers[2].start({ display: "block" });
                //   controllers[2].start({
                //     height: "800px",
                //     opacity: 1,
                //   });
                // } else if (i === 3) {
                //   await AnimatDashbord.start({ height: "600px" });
                //   await controllers[3].start({ display: "block" });
                //   controllers[3].start({
                //     height: "600px",
                //     opacity: 1,
                //   });
                // } else if (i === 4) {
                //   await AnimatDashbord.start({ height: "1000px" });
                //   await controllers[4].start({ display: "block" });
                //   controllers[4].start({
                //     height: "1000px",
                //     opacity: 1,
                //   });
                // }
                // selected = i;
                // controllers[selected].start({ height: 0, opacity: 0 });
                //}
              }}
            >
              {e}
            </motion.span>
          );
        })}
      </div>
      <div className="view">
        <AnimatePresence>
          {view === "dash board" && <DashbordDashbord key={0} />}
          {view === "Posts" && <DashbordPost key={1} />}
          {view === "Edit profile" && <DashbordEditProfileComponent key={2} />}
          <DashbordPackInfo controller={controllers[3]} />
          <DashbordAdressComponent controller={controllers[4]} />
        </AnimatePresence>
      </div>
    </DshbordComponenetStyle>
  );
};

export default DashbordComponenet;
