import styled from "styled-components";
import { motion, useAnimation } from "framer-motion";
import { Link } from "react-router-dom";
import { auto } from "async";

const NavbarHorizontalStyle = styled(motion.div)`
  width: 100vw;
  height: 10vw;
  z-index: 20000;
  position: fixed;
  top: 0;
  left: 0;
  display: none;
  background-color: #191919;
  .icon {
    position: absolute;
    height: 7vw;
    width: 7vw;
    top: 1.5vw;
    right: 5vw;
    background-color: green;
    border-radius: 100%;
  }
  .logo {
    position: absolute;
    display: flex;
    left: 5vw;
    justify-content: center;
    align-items: center;
    height: 100%;
    color: #f2c14e;
    cursor: pointer;
    display: flex;
    font-size: 5vw;
  }
  .navbar {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    padding-top: 10vw;
    padding-left: 5vw;
    height: 110vh;
    padding-bottom: 20vw;
    background-color: rgba(25, 25, 25, 1);
    z-index: 10000;
    display: flex;
    flex-direction: column;
    color: #f8f8ff;
    overflow: auto;
    .elmnt {
      margin-top: 30px;
      display: flex;
      flex-direction: column;
      .content {
        display: flex;
      }
      .downItem {
        height: auto;
        width: auto;
        margin-left: 5vw;
        font-size: 20px;
        display: flex;
        flex-direction: column;
        height: auto;
        display: none;
        div {
          margin-top: 20px;
        }
        .dropDownTitle {
          margin-top: 20px;
          color: #f8f8ff;
          font-size: 30px;
          text-decoration: none;
        }
      }
      .downIcon {
        cursor: pointer;
        margin-left: 50px;
        height: 50px;
        width: 50px;
        background-color: sandybrown;
        border-radius: 100%;
      }
      .name {
        cursor: pointer;
        color: #f2c14e;
        font-size: 50px;
      }
    }
  }
  @media (min-width: 800px) {
    height: 100px;
    .icon {
      height: 50px;
      width: 50px;
      top: 25px;
      background-color: lavender;
    }
    .logo {
      font-size: 40px;
    }
  }
  @media (max-width: 1100px) {
    display: block;
  }
`;

const NavbarContent = [
  "home",
  "Five cols",
  "Fewer cols",
  "Templates",
  "Other pages",
];

const Home = ["demo style 1", "demo style 2", "demo style 2"];
const HomePaths = ["/", "home2", "home3"];
const Templates = [
  "About",
  "Contact us",
  "Login",
  "Signup",
  "Forgot Password",
  "Dashboard",
];
const TemplatePaths = ["/aboute", "/contactus", "/", "/", "/", "/dashboard"];

const OtherPages = [
  "Archive Page",
  "Author Page",
  "Search Page",
  "articles Template",
];
const OtherPagesPaths = ["/topics", "/author", "/search", "/article"];

const DownElmnts = {
  home: [Home, HomePaths],
  "Five cols": [],
  "Fewer cols": [],
  Templates: [Templates, TemplatePaths],
  "Other pages": [OtherPages, OtherPagesPaths],
};

const DropDownComponent = (name) => {
  const elemnt = DownElmnts[name];
  if (elemnt.length > 0) {
    return elemnt[0].map((e, i) => {
      return (
        <div key={i}>
          <Link to={elemnt[1][i]} className="dropDownTitle" key={i}>
            {e}
          </Link>
        </div>
      );
    });
  }
};

const NavbarHorizontal = () => {
  const controllers = {
    0: useAnimation(),
    1: useAnimation(),
    2: useAnimation(),
    3: useAnimation(),
    4: useAnimation(),
  };
  const controllers2 = {
    0: useAnimation(),
    1: useAnimation(),
    2: useAnimation(),
    3: useAnimation(),
    4: useAnimation(),
  };
  let isShowed = {
    0: false,
    0: false,
    0: false,
    0: false,
    0: false,
  };
  return (
    <NavbarHorizontalStyle>
      <div className="icon" />
      <span className="logo"> DeepEnd </span>
      <div className="navbar">
        {Object.keys(DownElmnts).map((e, i) => {
          return (
            <div className="elmnt" key={i}>
              <div className="content">
                <span className="name"> {e} </span>
                <motion.div
                  animate={controllers2[i]}
                  onClick={() => {
                    if (!isShowed[i]) {
                      console.log("hereee");
                      controllers[i].start({
                        height: "auto",
                      });
                      controllers[i].start({
                        display: "block",
                      });
                      isShowed[i] = true;
                    } else {
                      controllers[i].start({
                        height: "0px",
                        opacity: 0,
                      });
                      controllers[i].start({
                        display: "none",
                      });
                      isShowed[i] = false;
                    }
                  }}
                  className={DownElmnts[e].length > 0 ? "downIcon" : ""}
                />
              </div>
              <motion.div className="downItem" animate={controllers[i]}>
                {DropDownComponent(e)}
              </motion.div>
            </div>
          );
        })}
      </div>
    </NavbarHorizontalStyle>
  );
};

export default NavbarHorizontal;
