import styled from "styled-components";
import { motion, useAnimation } from "framer-motion";

const SearchComponentStyle = styled(motion.div)`
  margin-top: 10vw;
  position: relative;
  display: flex;
  flex-direction: column;
  margin-left: 5vw;
  width: 90vw;
  .searchBar {
    display: flex;
    padding: 2vw;
    width: 50vw;
    background-color: #f8f8ff;
    input {
      outline: none;
      width: 100%;
      font-size: 20px;
      color: #191919;
      padding-left: 1vw;
      border: none;
      background-color: #f8f8ff;
      border-bottom: 2px solid #191919;
    }
    input::placeholder {
      outline: none;
      font-size: 20px;
      color: #191919;
    }
    input:focus {
      outline: none;
      border: none;
      border-bottom: 2px solid red;
    }
    .icon {
      height: 50px;
      width: 50px;
      min-height: 50px;
      min-width: 50px;
      background-color: aqua;
      margin-right: 3vw;
      border-radius: 100%;
    }
  }
  .suggest {
    height: auto;
    width: 50vw;
    display: flex;
    flex-direction: column;
    padding: 0;
    .title {
      padding: 1vw;
      font-size: 24px;
      color: #679267;
      opacity: 0;
      background-color: #f8f8ff;
    }
    .result {
      background-color: aqua;
      font-size: 24px;
      opacity: 0;
      max-height: 10vw;
      color: #1f1f1f;
      background-color: #f8f8ff;
      height: calc(24px + 2vw);
      span {
        height: auto;
        cursor: pointer;
        padding: 1vw;
      }
      .span {
        height: auto;
        padding: 1vw;
        cursor: pointer;
        color: #8a8888;
        font-size: 20px;
      }
    }
  }
`;

const SearchComponent = () => {
  const controller = useAnimation();

  return (
    <SearchComponentStyle>
      <div className="searchBar">
        <div className="icon" />
        <input
          placeholder={"react"}
          onClick={() => {
            controller.start({ opacity: 1 });
          }}
        />
      </div>
      <motion.div
        className="suggest"
        animate={controller}
        transition={{
          duration: 0.3,
        }}
      >
        <motion.span
          className="title"
          animate={controller}
          transition={{
            duration: 0.3,
          }}
        >
          suggetion
        </motion.span>
        <motion.span
          className="result"
          animate={controller}
          whileHover={{ backgroundColor: "green" }}
          transition={{
            height: { delay: 0.2, duration: 0.5 },
            opacity: { delay: 0.25, duration: 0.5 },
          }}
        >
          <span>top technologie</span>
          <span className="span"> 201 article </span>
        </motion.span>
        <motion.span
          className="result"
          animate={controller}
          transition={{
            height: { delay: 0.5, duration: 0.5 },
            opacity: { delay: 0.6, duration: 0.5 },
          }}
        >
          <span>modern design</span> <span className="span">21 article </span>
        </motion.span>

        <motion.span
          className="result"
          animate={controller}
          transition={{
            height: { delay: 0.8, duration: 0.5 },
            opacity: { delay: 0.95, duration: 0.5 },
          }}
        >
          <span>responsive websites</span>
          <span className="span"> 150 article </span>
        </motion.span>
      </motion.div>
    </SearchComponentStyle>
  );
};
export default SearchComponent;
