import styled from "styled-components";
import { motion } from "framer-motion";

const DashbordAdressComponentStyle = styled(motion.div)`
  position: absolute;
  top: 0;
  margin-top: 100px;
  height: 0;
  width: 100%;
  opacity: 0;
  overflow: hidden;
  .profileDetails {
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
    div {
      display: flex;
      flex-direction: column;
      margin: 20px;
      margin-bottom: 50px;
      width: 40vw;
      margin-left: 20px;
      span {
        font-size: 20px;
        color: #f2c14e;
      }
      input {
        padding: 10px;
        border: none;
        outline: none;
        background-color: transparent;
        border-bottom: 2px solid #8a8888;
        color: #f8f8ff;
        font-size: 20px;
      }
      input :placeholder {
        font-size: 16px;
      }
      input :focus {
        border: none;
        outline: none;
      }
    }
  }
  .email {
    display: flex;
    flex-direction: column;
    margin: 20px;
    margin-bottom: 50px;
    width: 40vw;
    margin-left: 20px;
    span {
      font-size: 20px;
      color: #f2c14e;
    }
    input {
      padding: 10px;
      border: none;
      outline: none;
      background-color: transparent;
      border-bottom: 2px solid #8a8888;
      color: #f8f8ff;
      font-size: 20px;
    }
    input :placeholder {
      font-size: 16px;
    }
    input :focus {
      border: none;
      outline: none;
    }
  }
  .update {
    margin-top: 20px;
    cursor: pointer;
    padding: 20px;
    width: 220px;
    background-color: #f2c14e;
    font-size: 20px;
  }
  select {
    padding: 10px;
    border: none;
    outline: none;
    background-color: transparent;
    border-bottom: 2px solid #8a8888;
    color: #f8f8ff;
    font-size: 20px;
  }
  select :placeholder {
    font-size: 16px;
  }
  select :focus {
    border: none;
    outline: none;
  }
  option {
    margin-top: 10px;
    font-size: 18px;
    background-color: #191919;
  }
`;
const DashbordAdressComponent = ({ controller }) => {
  return (
    <DashbordAdressComponentStyle animate={controller}>
      <div className="profileDetails">
        <div>
          <span> Country </span>
          <select>
            <option>Uninted state</option>
            <option value="saab">Canada</option>
            <option value="fiat">Mexico</option>
            <option value="audi">Russia</option>
            <option value="fiat">Japan</option>
            <option value="audi">Germany</option>
          </select>
        </div>
        <div>
          <span> State/Province/Region * </span>
          <select>
            <option value="volvo">New york</option>
            <option value="saab">Los angelos</option>
            <option value="fiat">California</option>
            <option value="audi">Wachenton</option>
          </select>
        </div>
        <div>
          <span> Address Line 1 * </span>
          <input placeholder="current password" />
        </div>
        <div>
          <span> Address Line 2</span>
          <input placeholder="new password" />
        </div>
        <div>
          <span> City *</span>
          <input placeholder="new password" />
        </div>
        <div>
          <span>City * Postal/ZIP Code * </span>
          <input placeholder="new password" />
        </div>
      </div>

      <div className="update"> Update Billing Address </div>
    </DashbordAdressComponentStyle>
  );
};

export default DashbordAdressComponent;
