import styled from "styled-components";
import { motion, useAnimation } from "framer-motion";
import DashbordDashbord from "./dashbord.dashbord.component";
import DashbordPost from "./dashbord.posts.component";
import DashbordEditProfileComponent from "./dashbord.edit.prodile.component";
import DashbordPackInfo from "./dashbord.pack.info.component";
import { useEffect } from "react";
import DashbordAdressComponent from "./dashbord.address.component";

const DshbordComponenetStyle = styled(motion.div)`
  display: flex;
  flex-direction: column;
  position: relative;
  height: auto;
  left: 5vw;
  width: 90vw;
  margin-top: 150px;
  .tags {
    position: relative;
    .tag {
      color: #8a8888;
      padding: 10px;
      margin: 10px;
      cursor: pointer;
    }
    .tag:first-child {
      margin-left: 0;
    }
  }
  .view {
    margin-top: 50px;
  }
  .viewModel {
    position: absolute;
    top: 0;
    margin-top: 50px;
    left: 0;
    opacity: 0;
    height: 100px;
    width: 200px;
    font-size: 50px;
    color: greenyellow;
    cursor: pointer;
  }
`;

const DashbordComponenet = () => {
  let selected = 0;
  let tagSelected = 0;

  let AnimatDashbord = useAnimation();

  let controllers = {
    0: useAnimation(),
    1: useAnimation(),
    2: useAnimation(),
    3: useAnimation(),
    4: useAnimation(),
    5: useAnimation(),
  };
  let tagsControllers = {
    0: useAnimation(),
    1: useAnimation(),
    2: useAnimation(),
    3: useAnimation(),
    4: useAnimation(),
    5: useAnimation(),
  };

  useEffect(async () => {
    tagsControllers[0].start({ color: "#f8f8f8" });
    await AnimatDashbord.start({ height: "100px" });
    controllers[0].start({ height: "200px", opacity: 1 });
  }, []);

  return (
    <DshbordComponenetStyle animate={AnimatDashbord}>
      <div className="tags">
        {[
          "Dash board",
          "Posts",
          "Edit profile",
          "Subscription",
          "Billing address",
          "Submit post",
        ].map((e, i) => {
          return (
            <motion.span
              animate={tagsControllers[i]}
              // whileHover={{ color: "#f8f8f8" }}
              className="tag"
              onClick={async () => {
                if (selected !== i) {
                  tagsControllers[i].start({ color: "#f8f8f8" });
                  tagsControllers[tagSelected].start({ color: "#8a8888" });

                  controllers[selected].start({ opacity: 0 });
                  if (i === 1) {
                    await AnimatDashbord.start({ height: "1100px" });
                    controllers[1].start({ height: "1100px", opacity: 1 });
                  } else if (i === 0) {
                    await AnimatDashbord.start({ height: "100px" });
                    controllers[0].start({ height: "200px", opacity: 1 });
                  } else if (i === 2) {
                    await AnimatDashbord.start({ height: "600px" });
                    controllers[2].start({
                      height: "600px",
                      opacity: 1,
                    });
                  } else if (i === 3) {
                    await AnimatDashbord.start({ height: "300px" });
                    controllers[3].start({
                      height: "300px",
                      opacity: 1,
                    });
                  } else if (i === 4) {
                    await AnimatDashbord.start({ height: 600 });
                    controllers[4].start({
                      height: "600px",
                      opacity: 1,
                    });
                  }
                  controllers[selected].start({ height: 0, opacity: 0 });
                  selected = i;
                }
              }}
            >
              {e}
            </motion.span>
          );
        })}
      </div>
      <div className="view">
        <DashbordDashbord controller={controllers[0]} />
        <DashbordPost controller={controllers[1]} />
        <DashbordEditProfileComponent controller={controllers[2]} />
        <DashbordPackInfo controller={controllers[3]} />
        <DashbordAdressComponent controller={controllers[4]} />
        {/* <TestView tag="Subscription" controllers={controllers[3]} />
        <TestView tag="Posts" controllers={controllers[1]} />
        <TestView tag="Edit profile" controllers={controllers[2]} />{" "}
        <TestView tag="Dash board" controllers={controllers[0]} /> */}
      </div>
    </DshbordComponenetStyle>
  );
};

export default DashbordComponenet;
