import styled from "styled-components";
import { motion } from "framer-motion";

const AuthorstListStyle = styled(motion.div)`
  position: relative;
  margin-top: 200px;
  width: 93vw;
  margin-left: 5vw;
  background-color: lightblue;
  height: auto;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  .Author {
    margin-right: 50px;
    margin-top: 50px;
    width: 280px;
    height: 340px;
    background-color: lightcoral;

    .AuthorBg {
      position: relative;
      top: 0;
      display: flex;
      flex-direction: column;
      align-items: flex-start;
      height: 170px;
      width: inherit;
    }
    .authorDeatils {
      position: relative;
      top: 0;
      height: 340px;
      width: 280px;
      background-color: black;
      display: flex;
      background-image: linear-gradient(black, white, white, white);
    }
  }
`;

const AuthorsList = () => {
  return (
    <AuthorstListStyle>
      {[1, 1, 1, 1, 1].map((e, i) => {
        return (
          <div className="Author">
            <div className="authorDeatils">
              <div className="AuthorPIc" />
              <span className="name">tahar belghitri</span>
              <span className="posts"> 201 posts </span>
              <span className="startedAt"> 02/02/2021 </span>
            </div>
          </div>
        );
      })}
    </AuthorstListStyle>
  );
};

export default AuthorsList;
