import styled from "styled-components";
import { motion } from "framer-motion";

const DashbordPostStyle = styled(motion.div)`
  position: absolute;
  margin-top: 100px;
  top: 0;
  left: 0;
  z-index: 100;
  height: 0px;
  overflow: clip;
  width: auto;
  background-color: #191919;
  opacity: 0;
  display: none;
  .titles {
    position: relative;
    width: 90vw;
    height: auto;
    display: flex;
    flex-wrap: nowrap;
    * {
      color: #f8f8ff;
      font-size: 20px;
    }
    .article {
      display: flex;
      width: 400px;
    }
    .status {
      width: 100px;
      margin-left: 25px;
    }
  }
  .content {
    color: #f8f8ff;
    margin-top: 50px;
    width: 90vw;
    position: relative;
    height: auto;
    display: flex;
    flex-wrap: nowrap;

    margin-bottom: 50px;
    .cover {
      height: 75px;
      width: 150px;
      background-color: lightgreen;
    }
    .title {
      white-space: pre-wrap;
      margin-left: 25px;
      width: 225px;
    }
    .status {
      width: 100px;
      color: #85ef90;
      margin-left: 25px;
    }
    .payment {
      width: 200px;
      color: #8a8888;
    }
    .more {
      background-color: lime;
      position: relative;
      top: -2.5px;
      height: 25px;
      width: 25px;
      border-radius: 100%;
    }
  }
  @media (max-width: 750px) {
    margin-top: 400px;
  }
`;
const DashbordPost = ({ controller }) => {
  return (
    <DashbordPostStyle animate={controller}>
      <div className="titles">
        <span className="article">Articles</span>
        <span className="status">Status</span>
        <span className="payment">Payment</span>
      </div>
      {[1, 1, 1, 1, 1, 1, 1, 1].map((e, i) => {
        return (
          <div key={i} className="content">
            <div className="cover" />
            <span className="title">
              Traveling Tends to Magnify All Human Emotions
            </span>
            <span className="status">Active</span>
            <span className="payment">Not Applicable</span>
            <div className="more" />
          </div>
        );
      })}
    </DashbordPostStyle>
  );
};
export default DashbordPost;
