import styled from "styled-components";
import { motion } from "framer-motion";

const SignComponentStyle = styled(motion.div)`
  margin-top: 10vw;
  margin-left: 5vw;
  display: flex;
  flex-direction: column;
  height: auto;
  width: 90vw;
  max-width: 2000px;
  .cover {
    position: absolute;
    right: 0;
    top: 0vw;
    height: 90vh;
    width: 40vw;
    background-color: azure;
    max-height: 800px;
    @media (min-width: 2000px) {
      width: 800px;
      right: calc(50vw - 1000px);
    }
  }
  .social {
    position: relative;
    display: flex;
    flex-direction: row;
    width: 40vw;
    align-items: center;
    font-size: 20px;
    margin-bottom: 3vw;
    align-items: center;
    max-width: 700px;
    div {
      position: relative;
      min-height: 30px;
      min-width: 30px;
      border-radius: 50px;
      background-color: black;
      margin-right: 6%;
    }
    span {
      margin-right: 10%;
      color: #f8f8ff;
    }
  }
  .title {
    font-size: 24px;
    color: #f8f8ff;
    margin-top: 40px;
    margin-bottom: 30px;
  }
  input {
    font-size: 20px;
    width: 30vw;
    max-width: 700px;
    padding: 10px;
    outline: none;
    background-color: transparent;
    border: 4px solid #8a8888;
    color: #f8f8ff;
    &::placeholder {
      color: #8a8888;
    }
    &:focus {
      color: #f8f8ff;
    }
  }
  .submit {
    margin-top: 5vw;
    span {
      margin-top: 5vw;
      background-color: #f2c14e;
      padding: 1.5vw;
      font-size: 24px;
      cursor: pointer;
    }
  }
  @media (min-width: 2000px) {
    width: 1850px;
    margin-left: calc(55vw - 1000px);
  }
  @media (max-width: 500px) {
    .social {
      flex-direction: column;
      align-items: flex-start;
      span {
        margin: 4vw 0 3vw 0;
      }
    }
  }
  @media (max-width: 800px) {
    input {
      width: 80%;
    }
    .cover {
      position: relative;
      width: 90%;
      margin-top: 10vw;
    }
  }
`;
const SignComponent = () => {
  return (
    <SignComponentStyle
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5, delay: 0.3 }}
      exit={{ opacity: 0 }}
    >
      {window.scrollTo(0, 0)}
      <div className="social">
        <div className="facebook" />
        <span>facebook</span>
        <div className="google" />
        <span>google</span>
        <div className="twitter" />
        <span>twitter</span>
      </div>
      <span className="title">Email Adress</span>
      <input placeholder="Email Adress" />
      <span className="title"> Password </span>
      <input placeholder="Password" type="password" />
      <div className="submit">
        <span> Continue </span>
      </div>
      <motion.div
        className="cover"
        initial={{ height: 0 }}
        animate={{ height: "90vw" }}
        transition={{ duration: 0.9, delay: 0.6 }}
        exit={{ height: 0 }}
      />
    </SignComponentStyle>
  );
};
export default SignComponent;
