import { motion } from "framer-motion";
import styled from "styled-components";

const HomeComponentStyle = styled(motion.div)`
  position: relative;
  left: 5vw;
  margin-top: 7vw;
  display: flex;
  flex-direction: row;
  height: 1000px;
  width: 90vw;
  font-size: 18px;
  background-color: green;
  .introBg {
    position: absolute;
    margin-top: 500px;
    background-image: url("https://images.pexels.com/photos/3304170/pexels-photo-3304170.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940");
    background-position: 0 -1000px;
    background-repeat: no-repeat;
    background-size: cover;
    background-clip: border-box;
    width: 90vw;
    height: 600px;
    max-width: 1440px;
    @media (max-width: 1350px) {
      background-position: 0 -800px;
    }
    @media (max-width: 1150px) {
      background-position: 0 -600px;
    }
    @media (max-width: 1150px) {
      background-position: 0 -400px;
    }
  }

  .intro {
    display: flex;
    flex-direction: column;
    height: 600px;
  }
  .logo {
    margin-top: 30px;
    font-size: 100px;
    margin-bottom: 30px;
    color: #f8f8ff;
  }
  .welcome {
    color: #c1c0c4;
    width: 300px;
  }

  .aboutUs {
    height: auto;
    width: 330px;
    position: absolute;
    bottom: 0;
    margin-top: 00px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    color: #c1c0c4;
  }

  .text {
    color: #c1c0c4;
    width: 100%;
  }
  .styled {
    margin-left: 10vw;
    margin-top: 130px;
    position: relative;
    height: 300px;
    display: flex;
    flex-direction: column;
    width: auto;
  }

  @media (max-width: 950px) {
    flex-direction: column;
  }
  @media (min-width: 2000px) {
    width: calc(2000px - 5vw);
    max-width: calc(2000px - 5vw);
    margin-left: calc(50vw - 1000px);
    * {
      max-width: calc(2000px - 5vw);
    }
  }
`;

const HomeComponent3 = () => {
  return (
    <HomeComponentStyle>
      <div className="intro">
        <span className="welcome"> welcome to </span>

        <span className="logo"> deepend </span>
      </div>
      <div className="styled">
        <span className="text">
          “We Become What We Think About.” <br /> -Earl Nightingale
        </span>

        <div className="aboutUs">
          <span>_ we gave our visitor what they exactly want to read </span>
          <span>
            _ simple title with deep content could make them read the articles
            three times or more
          </span>
          <span>
            _ many articles type because of the good trafic we have and our
            newest authors ebery day
          </span>
        </div>
      </div>
      <div className="introBg" />
    </HomeComponentStyle>
  );
};
export default HomeComponent3;
