import styled from "styled-components";
import { motion, useAnimation } from "framer-motion";

const SearchComponentStyle = styled(motion.div)`
  margin-top: 10vw;
  position: relative;
  display: flex;
  flex-direction: column;
  margin-left: 5vw;
  background-color: black;
  width: 90vw;
  .searchBar {
    display: flex;
    padding: 2vw;
    background-color: beige;
    width: 50vw;
    input {
      outline: none;
      width: 100%;
      font-size: 20px;
      color: #191919;
      padding-left: 1vw;
      border: none;
      background-color: transparent;
      border-bottom: 2px solid transparent;
    }
    input::placeholder {
      outline: none;
      font-size: 20px;
      color: #191919;
      padding-left: 1vw;
    }
    input:focus {
      outline: none;
      border: none;
    }
    .icon {
      height: 50px;
      width: 50px;
      min-height: 50px;
      min-width: 50px;
      background-color: aqua;
      margin-right: 3vw;
      border-radius: 100%;
    }
  }
  .suggest {
    height: auto;
    width: 50vw;
    background-color: blue;
    height: auto;
    display: flex;
    flex-direction: column;
    .result {
      padding: 1vw;

      font-size: 24px;
      opacity: 0;
      height: 0;
      max-height: 10vw;
      span {
        font-size: 20px;
      }
    }
  }
`;

const SearchComponent = () => {
  const controller = useAnimation();

  return (
    <SearchComponentStyle>
      <div className="searchBar">
        <div className="icon" />
        <input
          placeholder={"react"}
          onClick={() => {
            controller.start({ height: "100%", opacity: 1 });
          }}
        />
      </div>
      <div className="suggest">
        <motion.span
          className="result"
          animate={controller}
          transition={{
            height: { delay: 0.2, duration: 0.2 },
            opacity: { delay: 0.25, duration: 0.2 },
          }}
        >
          programmers <span> 201 article </span>
        </motion.span>
        <motion.span
          className="result"
          animate={controller}
          transition={{
            height: { delay: 0.5, duration: 0.2 },
            opacity: { delay: 0.6, duration: 0.2 },
          }}
        >
          css <span> 21 article </span>
        </motion.span>
        <motion.span
          className="result"
          animate={controller}
          transition={{
            height: { delay: 0.8, duration: 0.2 },
            opacity: { delay: 0.95, duration: 0.2 },
          }}
        >
          webiste <span> 150 article </span>
        </motion.span>
      </div>
    </SearchComponentStyle>
  );
};
export default SearchComponent;
