import styled from "styled-components";
import { motion } from "framer-motion";

const AbouteComponentStyle = styled(motion.div)`
  margin-top: 300px;
  position: relative;
  width: 90vw;
  height: auto;
  left: 5vw;
  display: flex;
  justify-content: flex-end;
  .text {
    z-index: 1000;
    position: absolute;
    left: 0;
    bottom: 0;
    width: 35vw;
    padding: 0;
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    .title {
      font-size: 100px;
      color: #f2c14e;
    }
    .subtitle {
      font-size: 18px;
      color: #f8f8ff;
    }
  }
  .cover {
    height: 400px;
    width: 1000px;
    background-color: gray;
  }
  margin-bottom: 300px;
`;
const AbouteComponent = ({ title = "We’re", subTitle = "", styles = "" }) => {
  return (
    <AbouteComponentStyle styles={styles}>
      <div className="text">
        <span className="title">{title}</span>
        <span className="subtitle">
          {subTitle === ""
            ? `impartial and independent, and every day we create distinctive,
          world-class programmes and content which inform, educate and entertain
          millions of people in the around the world.`
            : subTitle}
        </span>
      </div>
      <div className="cover" />
    </AbouteComponentStyle>
  );
};

export default AbouteComponent;
