import styled from "styled-components";
import { motion } from "framer-motion";
import { useHistory } from "react-router-dom";
import TopicsData from "../authorsData.json";

const TopicsStyle = styled(motion.div)`
  margin-top: 150px;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  width: 90vw;
  margin-left: 5vw;
  .topic {
    height: 240px;
    width: 200px;
    margin-right: 40px;
    margin-bottom: 40px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    padding: 10px;
    .details {
      height: 90px;
      display: flex;
      flex-direction: column;
      justify-content: space-around;
      .title {
        font-size: 24px;
        color: #f8f8ff;
        cursor: pointer;
      }
      .postsNUmber {
        font-size: 16px;
        color: #8a8888;
        cursor: pointer;
      }
    }
    .topicCover {
      width: 200px;
      height: 150px;
      /* background-image: url("https://images.pexels.com/photos/3178798/pexels-photo-3178798.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"); */
      background-position: center;
      background-repeat: no-repeat;
      background-size: cover;
      background-clip: border-box;
      cursor: pointer;
    }
  }
  /* @media (max-width: 1200px) {
    flex-direction: column;
    background-color: #f8f8ff;
    .topic {
      width: 90vw;
      .topicCover {
        width: 40vw;
      }
    }
  } */
  /* @media (max-width: 800px) {
    .topic {
      width: 140px;
      margin-right: 20px;
      .topicCover {
        width: 150px;
      }
    }
  } */
  @media (min-width: 2000px) {
    width: calc(2000px - 5vw);
    margin-left: calc(55vw - 1000px);
  }
`;

const Topics = () => {
  const history = useHistory();
  return (
    <TopicsStyle
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5, delay: 0.9 }}
      onClick={() => history.push("/topics")}
    >
      {TopicsData.topics.map((e, i) => {
        return (
          <motion.div className="topic" key={i} whileHover={{ scale: 1.1 }}>
            <div className="details">
              <span className="title">{e.topic}</span>
              <span className="postsNUmber">
                {" "}
                {Math.floor(Math.random() * 1000)} post{" "}
              </span>
            </div>
            <div
              className="topicCover"
              style={{ backgroundImage: "url('" + e.topicCver + "')" }}
            />
          </motion.div>
        );
      })}
    </TopicsStyle>
  );
};

export default Topics;
